import { useState } from "react";
import { useDispatch } from "react-redux";
import DropDown from "./DropDown/dropDown";
import Classes from "./SerchBar.module.css";

const Search = (props) => {
  const dispach = useDispatch();
  const [inputValue, setInputValue] = useState("");
  const [isSearchOk, setIsSearchOk] = useState(null);
  const inputHandler = (event) => {
    let value = event.target.value;
    for (let i = 0; i < value.length; i++) {
      let temp = value.charAt(i);
      if (!(96 < temp > 122) || !(65 < temp > 90)) {
        setIsSearchOk(false);
        break;
      }
      if (i === value.length) {
        setIsSearchOk(true);
      }
    }
    console.log(isSearchOk);
    event.target.value.slice();
    setInputValue(event.target.value);
  };

  const submitHandler = (event, value) => {
    event.preventDefault();
    setInputValue("");
    dispach({ type: "changeLocation", payload: value });
  };
  return (
    <div className={Classes.searchContainer}>
      <form onSubmit={submitHandler} className={Classes.Search}>
        <button className="Button" type="submit"></button>
        <input
          onChange={inputHandler}
          placeholder="Search here"
          value={inputValue}
          lang="en"
          type="text"
        ></input>
        <p></p>
      </form>
      <DropDown inputVal={inputValue} autoComplete={submitHandler} />
    </div>
  );
};

export default Search;
